        }
    }


    function CleanPhoneNumber($phone_number)
    {
        $pn = trim(preg_replace('[\D]', '', $phone_number));
        // $pn_len = strlen($pn);
      
        if ('00' == substr($pn,0, 2))
        {
            $pn = substr($pn, 2);
        }
        elseif ('0' == substr($pn,0, 1))
        {
            $pn = $this->GetTelDefaultCountryCode() . substr($pn, 1);
        }

        return $pn;
