    function SetNtKey($hex_value)
    {
        $temp = $hex_value;
        if (16 == strlen($temp))
        {
            $temp = bin2hex($temp);
        }
        $pos = strpos(strtolower($temp), 'x');
        if (FALSE !== $pos)
        {
            $temp = substr($temp, $pos+1);
        }
        if (32 != strlen($temp))
        {
            $temp = '';
        }
        $this->_ms_nt_key = strtoupper($temp);
    }


    function GetNtKey()
    {
        $temp = $this->_ms_nt_key;
        if (16 == strlen($temp))
        {
            $temp = bin2hex($temp);
        }
        elseif (32 != strlen($temp))
        {
            $temp = '';
        }
        return strtoupper($temp);
    }


