    function GetLdapUsersList()
    {
        $this->DisableLdapError();
        $users_list = '';
        $in_groups_array = array();
        $result_array = array();
        
        if (!function_exists('ldap_connect'))
        {
            $result = FALSE;
            $this->WriteLog("Error: PHP LDAP library is not installed", FALSE, FALSE, 299, 'System', '');
            $this->EnableLdapError();
        }
        elseif (('' != $this->GetLdapDomainControllers()) && ('' != $this->GetLdapBindDn()) && ('' != $this->GetLdapServerPassword()))
        {
            $domain_controllers = explode(" ",trim(str_replace(","," ",str_replace(";"," ",$this->GetLdapDomainControllers()))));
            $ldap_options = array('account_suffix'     => $this->GetLdapAccountSuffix(),
                                  'ad_password'        => $this->GetLdapServerPassword(),
                                  'ad_username'        => $this->GetLdapBindDn(),
                                  'base_dn'            => $this->GetLdapBaseDn(),
                                  'cn_identifier'      => $this->GetLdapCnIdentifier(),
                                  'domain_controllers' => $domain_controllers,
                                  'group_attribute'    => $this->GetLdapGroupAttribute(),
                                  'network_timeout'    => $this->GetLdapNetworkTimeout(),
                                  'port'               => $this->GetLdapPort(),
                                  'recursive_groups'   => TRUE,
                                  'time_limit'         => $this->GetLdapTimeLimit(),
                                  'use_ssl'            => $this->IsLdapSsl()
                                 );

            $ldap_connection=new MultiotpAdLdap($ldap_options);
            if ($users_info = $ldap_connection->users_info('*',
                                                           array($this->GetLdapCnIdentifier(),"mail",$this->GetLdapGroupAttribute(),"department","displayname","telephonenumber","primarygroupid","mobile","msNPAllowDialin","userAccountControl")))
            {
                if ($ldap_connection->IsError())
                {
                    $this->EnableLdapError();
                }
                $all_results = (isset($users_info['count'])?$users_info['count']:0);
                for ($results=0; $results < $all_results; $results++)
                {
                    $one_user = $users_info[$results];
                    $user = decode_utf8_if_needed(isset($one_user[strtolower($this->GetLdapCnIdentifier())][0])?($one_user[strtolower($this->GetLdapCnIdentifier())][0]):'');
                    $useraccountcontrol = (isset($one_user['useraccountcontrol'][0])?($one_user['useraccountcontrol'][0]):0);
                    $accountdisable = ((0 != ($useraccountcontrol & 2))?TRUE:FALSE);
                    
                    if (!$accountdisable)
                    {
                        if ('' == trim($this->GetLdapInGroup()))
                        {
                            $in_a_group = TRUE;
                        }
                        else
                        {
                            $in_a_group = FALSE;
                            $in_groups_array_raw = explode(" ",trim(str_replace(","," ",str_replace(";"," ",strtolower($this->GetLdapInGroup())))));
                            foreach($in_groups_array_raw as $one_group)
                            {
                                $in_groups_array[] = trim($one_group);
                            }
                        }

                        $groups_array_raw = $ldap_connection->user_groups($user);
                        $groups_array = array();
                        foreach($groups_array_raw as $one_group)
                        {
                            $this_group = decode_utf8_if_needed($one_group);
                            $groups_array[] = $this_group;
                            if (in_array(strtolower($this_group), $in_groups_array))
                            {
                                $in_a_group = TRUE;
                            }
                        }

                        if ($in_a_group)
                        {
                            $users_list.= (('' != $users_list)?"\t":'').$user;
                        }
                    }
                }
            }
            else
            {
                $this->EnableLdapError();
                $this->WriteLog("Error: no LDAP binding", FALSE, FALSE, 30, 'LDAP', '');
            }
        }
        else
        {
            $this->WriteLog("Error: No LDAP connection information", FALSE, FALSE, 30, 'LDAP', '');
        }
        return $users_list;
    }


    function GetLdapUsersInfoArray($user_filter = "*", $include_disabled = TRUE, $ignore_in_group = FALSE)
    {
        $this->DisableLdapError();
        $in_groups_array = array();
        $result_array = array();

        if (!function_exists('ldap_connect'))
        {
            $result = FALSE;
            $this->WriteLog("Error: PHP LDAP library is not installed", FALSE, FALSE, 299, 'System', '');
            $this->EnableLdapError();
        }
        elseif (('' != $this->GetLdapDomainControllers()) && ('' != $this->GetLdapBindDn()) && ('' != $this->GetLdapServerPassword()))
        {
            $domain_controllers = explode(" ",trim(str_replace(","," ",str_replace(";"," ",$this->GetLdapDomainControllers()))));
            $ldap_options = array('account_suffix'     => $this->GetLdapAccountSuffix(),
                                  'ad_password'        => $this->GetLdapServerPassword(),
                                  'ad_username'        => $this->GetLdapBindDn(),
                                  'base_dn'            => $this->GetLdapBaseDn(),
                                  'cn_identifier'      => $this->GetLdapCnIdentifier(),
                                  'domain_controllers' => $domain_controllers,
                                  'group_attribute'    => $this->GetLdapGroupAttribute(),
                                  'network_timeout'    => $this->GetLdapNetworkTimeout(),
                                  'port'               => $this->GetLdapPort(),
                                  'recursive_groups'   => TRUE,
                                  'time_limit'         => $this->GetLdapTimeLimit(),
                                  'use_ssl'            => $this->IsLdapSsl()
                                 );

            $ldap_connection=new MultiotpAdLdap($ldap_options);
            if ($users_info = $ldap_connection->users_info($user_filter,
                                                           array($this->GetLdapCnIdentifier()
                                                           ,"mail",
                                                           $this->GetLdapGroupAttribute(),
                                                           "department",
                                                           "displayname",
                                                           "telephonenumber",
                                                           "primarygroupid",
                                                           "mobile",
                                                           "msNPAllowDialin",
                                                           "userAccountControl")))
            {
                if ($ldap_connection->IsError())
                {
                    $this->EnableLdapError();
                }
                $all_results = (isset($users_info['count'])?$users_info['count']:0);
                for ($results=0; $results < $all_results; $results++)
                {
                    $one_user = $users_info[$results];
                    $user = decode_utf8_if_needed(isset($one_user[strtolower($this->GetLdapCnIdentifier())][0])?($one_user[strtolower($this->GetLdapCnIdentifier())][0]):'');
                    if (!$this->IsCaseSensitiveUsers())
                    {
                        $user = strtolower($user);
                    }
                    $useraccountcontrol = (isset($one_user['useraccountcontrol'][0])?($one_user['useraccountcontrol'][0]):0);
                    $accountdisable = ((0 != ($useraccountcontrol & 2))?TRUE:FALSE);
                    
                    if ($include_disabled || (!$accountdisable))
                    {
                        if ('' == trim($this->GetLdapInGroup()))
                        {
                            $in_a_group = TRUE;
                        }
                        else
                        {
                            $in_a_group = FALSE;
                            $in_groups_array_raw = explode(" ",trim(str_replace(","," ",str_replace(";"," ",strtolower($this->GetLdapInGroup())))));
                            foreach($in_groups_array_raw as $one_group)
                            {
                                $in_groups_array[] = trim($one_group);
                            }
                        }

                        $groups_array_raw = $ldap_connection->user_groups($user);
                        $groups_array = array();
                        foreach($groups_array_raw as $one_group)
                        {
                            $this_group = decode_utf8_if_needed($one_group);
                            $groups_array[] = $this_group;
                            if (in_array(strtolower($this_group), $in_groups_array))
                            {
                                $in_a_group = TRUE;
                            }
                        }

                        if ($ignore_in_group || $in_a_group)
                        {
                            $result_array[$user]['user'] = $user;
                            $result_array[$user]['groups'] = $groups_array;
                            $result_array[$user]['useraccountcontrol'] = $useraccountcontrol;
                            $result_array[$user]['accountdisable'] = ((0 != ($result_array[$user]['useraccountcontrol'] & 2))?TRUE:FALSE);
                            $result_array[$user]['mail'] = (isset($one_user['mail'][0])?decode_utf8_if_needed($one_user['mail'][0]):"");
                            $result_array[$user]['displayname'] = (isset($one_user['displayname'][0])?decode_utf8_if_needed($one_user['displayname'][0]):"");
                            $result_array[$user]['mobile'] = (isset($one_user['mobile'][0])?decode_utf8_if_needed($one_user['mobile'][0]):"");
                            $result_array[$user]['msnpallowdialin'] = ("TRUE" == (isset($one_user['msnpallowdialin'][0])?($one_user['msnpallowdialin'][0]):"FALSE"));
                            
                            // print_r($one_user); // DEBUG
                            /*
                            if ($user_groups = $ldap_connection->user_groups($user))
                            {
                                print_r($user_groups); // DEBUG
                            }
                            if ($ldap_connection->user_ingroup($user, 'VLAN_USERS'))
                            {
                                echo "DEBUG : In the group!\n";
                            }
                            */
                        }
                    }
                }
            }
            else
            {
                $this->EnableLdapError();
                $this->WriteLog("Error: LDAP connection failed", FALSE, FALSE, 30, 'LDAP', '');
            }
        }
        else
        {
            $this->EnableLdapError();
            $this->WriteLog("Error: no LDAP connection information", FALSE, FALSE, 30, 'LDAP', '');
        }
        return $result_array;
    }


    function TestLdapUser($value)
    {
        $result = FALSE;
        $user_to_check = ($this->IsCaseSensitiveUsers()?$value:strtolower($value));
        $ldap_users_array = $this->GetLdapUsersInfoArray();
        if (!$this->IsLdapError())
        {
            foreach($ldap_users_array as $one_ldap_user)
            {
                $user = $one_ldap_user['user'];
                $user = ($this->IsCaseSensitiveUsers()?$user:strtolower($user));
                if ($user_to_check == $user)
                {
                    $result = TRUE;
                    break;
                }
            }
        }
        return $result;
    }


    function SyncLdapUsers()
    {
        $ldap_begin_time = time();
        $result = FALSE;
        if ($this->IsLdapActivated())
        {
            $create_user_array = array();
            $update_user_array = array();

            $multiotp_users_array = $this->GetDetailedUsersArray();
            $ldap_users_array     = $this->GetLdapUsersInfoArray();

            if ($this->IsLdapError())
            {
                $this->WriteLog("Info: LDAP access error", FALSE, FALSE, 99, 'LDAP', '');
                $result = FALSE;
            }
            else
            {
                $result = TRUE;
                foreach($ldap_users_array as $one_ldap_user)
                {
                    $user = $one_ldap_user['user'];
                    if (!$this->IsCaseSensitiveUsers())
                    {
                        $user = strtolower($user);
                    }
                    $ldap_description = trim($one_ldap_user['displayname']);
                    $ldap_email = trim($one_ldap_user['mail']);
                    $ldap_enabled = (!$one_ldap_user['accountdisable']);
                    $ldap_sms = trim($one_ldap_user['mobile']);
                    // Detect and update LDAP/AD synchronized users
                    if (isset($multiotp_users_array[$user]))
                    {
                        $update_user = FALSE;
                        $multiotp_description  = trim($multiotp_users_array[$user]['description']);
                        $multiotp_email        = trim($multiotp_users_array[$user]['email']);
                        $multiotp_enabled      = $multiotp_users_array[$user]['enabled'];
                        $multiotp_sms          = trim($multiotp_users_array[$user]['sms']);
                        $multiotp_synchronized = $multiotp_users_array[$user]['synchronized'];
                        
                        if ($multiotp_synchronized)
                        {
                            if (('' != $ldap_description) && ($multiotp_description != $ldap_description))
                            {
                                $update_user_array[$user]['user']        = $user;
                                $update_user_array[$user]['description'] = $ldap_description;
                            }
                            if (('' != $ldap_email) && ($multiotp_email != $ldap_email))
                            {
                                $update_user_array[$user]['user']        = $user;
                                $update_user_array[$user]['email']       = $ldap_email;
                            }
                            if (('' != $ldap_sms) && ($multiotp_sms != $ldap_sms))
                            {
                                $update_user_array[$user]['user']        = $user;
                                $update_user_array[$user]['sms']         = $ldap_sms;
                            }
                            if ($multiotp_enabled != $ldap_enabled)
                            {
                                $update_user_array[$user]['user']        = $user;
                                $update_user_array[$user]['enabled']     = $ldap_enabled?1:0;
                            }
                        }
                    }
                    // Detect and create new LDAP/AD users
                    else
                    {
                        $create_user_array[$user]['user']        = $user;
                        $create_user_array[$user]['description'] = $ldap_description;
                        $create_user_array[$user]['email']       = $ldap_email;
                        $create_user_array[$user]['enabled']     = $ldap_enabled?1:0;
                        $create_user_array[$user]['sms']         = $ldap_sms;
                    }
                }
                foreach($multiotp_users_array as $one_multiotp_user)
                {
                    $user = $one_multiotp_user['user'];
                    if (!$this->IsCaseSensitiveUsers())
                    {
                        $user = strtolower($user);
                    }
                    $multiotp_enabled      = $one_multiotp_user['enabled'];
                    $multiotp_synchronized = $one_multiotp_user['synchronized'];
                    // Detect and disable LDAP/AD unexistant enabled synchronized users
                    if ($multiotp_synchronized && $multiotp_enabled)
                    {
                        if (!isset($ldap_users_array[$user]))
                        {
                            $update_user_array[$user]['user']    = $user;
                            $update_user_array[$user]['enabled'] = 0;
                        }
                    }
                }

                // Users to disable
                foreach($update_user_array as $one_user)
                {
                    if (0 == (isset($one_user['enabled'])?$one_user['enabled']:1))
                    {
                        $this->SetUser($one_user['user']);
                        foreach($one_user as $key => $value)
                        {
                            switch ($key)
                            {
                                case 'description':
                                    $this->SetUserDescription($value);
                                    break;
                                case 'email':
                                    $this->SetUserEmail($value);
                                    break;
                                case 'enabled':
                                    $this->SetUserActivated($value);
                                    break;
                                case 'sms':
                                    $this->SetUserSms($value);
                                    break;
                            }
                        }
                        $this->WriteUserData();
                    }
                }

                // Users not to be disabled
                foreach($update_user_array as $one_user)
                {
                    if (1 == (isset($one_user['enabled'])?$one_user['enabled']:1))
                    {
                        $this->SetUser($one_user['user']);
                        foreach($one_user as $key => $value)
                        {
                            switch ($key)
                            {
                                case 'description':
                                    $this->SetUserDescription($value);
                                    break;
                                case 'email':
                                    $this->SetUserEmail($value);
                                    break;
                                case 'enabled':
                                    $this->SetUserActivated($value);
                                    break;
                                case 'sms':
                                    $this->SetUserSms($value);
                                    break;
                            }
                        }
                        $this->WriteUserData();
                    }
                }

                // Users to create
                foreach($create_user_array as $one_user)
                {
                    $this->FastCreateUser($one_user['user'],
                                          $one_user['email'],
                                          $one_user['sms'],
                                          -1, // Prefix pin needed
                                          "totp",
                                          $one_user['enabled'],
                                          $one_user['description'],
                                          $this->GetDefaultUserGroup(), // Group
                                          1,  // Synchronized
                                          '', // Pin
                                          TRUE // Automated
                                         );
                }

                $time_info = gmdate("H:i:s", time()-$ldap_begin_time);
                $info_txt = '';

                if (count($update_user_array) > 0)
                {
                    $ldap_counter_suffix = ((1 < count($update_user_array))?'s':'');
                    $info_txt.= count($update_user_array)." user$ldap_counter_suffix updated";
                }
                if (count($create_user_array) > 0)
                {
                    if ('' != $info_txt)
                    {
                        $info_txt.= ', ';
                    }
                    $ldap_counter_suffix = ((1 < count($create_user_array))?'s':'');
                    $info_txt.= count($create_user_array)." user$ldap_counter_suffix created";
                }
                if ('' == $info_txt)
                {
                    $info_txt = 'No modification about LDAP users';
                }
                $this->WriteLog("Info: $info_txt (processed in $time_info)", FALSE, FALSE, 19, 'LDAP', '');
            }
        }
        return $result;
    }


    function CheckLdapAuthentication()
    /**
     * @brief   Write information in the log file/database, to the syslog server and on the screen
     *
     * @retval  boolean   Result of the test
     *
     * @author  Andre Liechti, SysCo systemes de communication sa, <info@multiotp.net>
     * @version 4.2.4.2
     * @date    2014-04-13
     * @since   2014-02-07
     */
    {
        $result = FALSE;
        if (!function_exists('ldap_connect'))
        {
            $this->WriteLog("Error: PHP LDAP library is not installed", FALSE, FALSE, 299, 'System', '');
        }
        elseif (('' != $this->GetLdapDomainControllers()) && ('' != $this->GetLdapBindDn()) && ('' != $this->GetLdapServerPassword()))
        {
            $domain_controllers = explode(" ",trim(str_replace(","," ",str_replace(";"," ",$this->GetLdapDomainControllers()))));
            $ldap_options = array('account_suffix'     => $this->GetLdapAccountSuffix(),
                                  'ad_password'        => $this->GetLdapServerPassword(),
                                  'ad_username'        => $this->GetLdapBindDn(),
                                  'base_dn'            => $this->GetLdapBaseDn(),
                                  'cn_identifier'      => $this->GetLdapCnIdentifier(),
                                  'domain_controllers' => $domain_controllers,
                                  'group_attribute'    => $this->GetLdapGroupAttribute(),
                                  'network_timeout'    => $this->GetLdapNetworkTimeout(),
                                  'port'               => $this->GetLdapPort(),
                                  'recursive_groups'   => TRUE,
                                  'time_limit'         => $this->GetLdapTimeLimit(),
                                  'use_ssl'            => $this->IsLdapSsl()
                                 );

            define('LDAP_OPT_DIAGNOSTIC_MESSAGE', 0x0032);
            
            $domain_controllers = explode(" ",trim(str_replace(","," ",str_replace(";"," ",$this->GetLdapDomainControllers()))));
            mt_srand(doubleval(microtime()) * 100000000); // for older php versions
            $domain_controller = ($domain_controllers[array_rand($domain_controllers)]);
            
            
            foreach($domain_controllers as $dc)
            {
                $port = $this->GetLdapPort();
                $controller = $dc;
                $protocol = "ldap://";
                if ($this->IsLdapSsl())
                {
                    $protocol = "ldaps://";
                }
                $pos = strpos($dc, "://");
                if ($pos !== FALSE)
                {
                    $protocol = substr($dc, 0, $pos+3);
                    $dc = substr($dc, $pos+3);
                }
                $pos = strpos($dc, ":");
                if ($pos !== FALSE)
                {
                    $port = substr($dc, $pos+1);
                    $dc = substr($dc, 0, $pos);
                }
                if ($ldapconn = @ldap_connect($protocol.$dc.":".$port))
                {
                    ldap_set_option($ldapconn, LDAP_OPT_PROTOCOL_VERSION, 3);
                    ldap_set_option($ldapconn, LDAP_OPT_REFERRALS, 0);
                    if (@ldap_bind($ldapconn, ($this->GetLdapBindDn().$this->GetLdapAccountSuffix()), ($this->GetLdapServerPassword())))
                    {
                        /*
                        if (ldap_get_option($ldapconn, LDAP_OPT_DIAGNOSTIC_MESSAGE, $extended_error))
                        {
                            echo "Error Binding to LDAP: $extended_error";
                        }
                        else
                        {
                            echo "Error Binding to LDAP: No additional information is available.";
                        }
                        */
                        $result = TRUE;
                    }
                    @ldap_unbind($ldapconn);
                }
                if ($result)
                {
                    break;
                }
            }
        }
        return $result;
    }


