// Clean quotes of the parameters if any
if (!function_exists('clean_quotes'))
{
    function clean_quotes($value)
    {
        $var = $value;
        if ((('"' == substr($var,0,1)) || ("'" == substr($var,0,1))) && (('"' == substr($var,-1)) || ("'" == substr($var,-1))))
        {
            $var = substr($var, 1, strlen($var)-2);
        }
        return $var;
    }
}


